import React, {Component, useState} from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

export default class TodoEdit extends Component {
    constructor(props) {
        super(props);

        this.onChangeTask = this.onChangeTask.bind(this);
        this.onChangeCompleted = this.onChangeCompleted.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            task: '',
            completed: false,
            dateCreated: new Date(),
            tasks: []
        }
    }

    componentDidMount() {
        axios.get('http://localhost:5000/task/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    task: response.data.task,
                    completed: response.data.completed,
                    dateCreated: new Date(response.data.dateCreated)
                })
            })
            .catch(function (error) {
                console.log(error);
            });

    }

    onChangeTask(e) {
        this.setState({
            task: e.target.value
        })
    }

    onChangeCompleted(e) {
        this.setState({
            completed: e.target.value
        })
    }

    onChangeDate(dateCreated) {
        // const date = useState(new Date())
        this.setState({
            dateCreated: dateCreated
        })
    }

    onSubmit(e) {
        e.preventDefault();

        const newTask = {
            task: this.state.task,
            completed: this.state.completed,
            dateCreated: this.state.dateCreated
        }

        console.log(newTask);

        axios.post('http://localhost:5000/task/update/'+this.props.params.id, newTask)
            .then(res => console.log(res.data));

        window.location = '/';
    }

    render() {
        return (
            <div>
                <h3>Edit Task</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Task: </label>
                        <input  type="text"
                                required
                                className="form-control"
                                value={this.state.task}
                                onChange={this.onChangeTask}
                        />

                    </div>
                    <div className="form-group">
                        <label>Status: </label>
                        <select required
                                className="form-control"
                                value={this.state.completed}
                                onChange={this.onChangeCompleted}>
                            <option value={true}>Completed</option>
                            <option value={false}>not Completed</option>

                            }
                        </select>
                    </div>
                    <div className="form-group">
                        <label>Date: </label>
                        <div>
                            {/*<input  type="text"*/}
                            {/*        required*/}
                            {/*        className="form-control"*/}
                            {/*        value={this.state.dateCreated}*/}
                            {/*        onChange={this.onChangeDate}*/}
                            {/*/>*/}
                            <DatePicker
                                selected={this.state.dateCreated}
                                onChange={dateCreated => this.onChangeDate.dateCreated}
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Edit" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
